import React from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import FormControl from "./FormControl";
import Row from "react-bootstrap/esm/Row";
import Col from "react-bootstrap/esm/Col";
import Button from "react-bootstrap/Button";

const baseURL = "https://localhost:7272/api/HR/v1/";

class UserForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      setShowForm: this.props.onBtnClick,
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      doorNo: "",
      streetName: "",
      city: "",
      state: "",
      country: "",
      pincode: "",
    };
    this.onChange = this.onChange.bind(this);

  }
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleSubmit = (event) => {
    console.log("here");
    let userInfo = {
      user: {
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        email: this.state.email,
        phone: this.state.phone,
      },
      address: [
        {
          doorNo: this.state.doorNo,
          streetName: this.state.streetName,
          city: this.state.city,
          state: this.state.state,
          country: this.state.country,
          pincode: this.state.pincode,
        },
      ],
    };
    console.log(userInfo);
    axios
      .post(baseURL+"User", {
        userInfo
      })
      .then((response) => {
        this.state.setShowForm();    
      }).catch(error => {
        let User = error.response.data.User
        let Addresses = error.response.data.Addresses
        User.forEach(element => {
            // element.forEach(elem =>{
                console.log(element)
            // })
        });
        Addresses.forEach(element => {
            // element.forEach(elem =>{
                console.log(element)
            // })
        });
      });
    
  };

  render() {
    return (
      <Form className="form-data" onSubmit={this.handleSubmit}>
        <Row>
          <Col xs={4}>
            <FormControl
              controlId="formFirstName"
              name="firstName"
              label="First Name"
              placeholder="Enter First Name"
              onChange = {this.onChange}
            ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formLastName"
              name="lastName"
              label="Last Name"
              placeholder="Enter Last Name"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
        </Row>
        <Row>
          <Col xs={4}>
            <FormControl
              controlId="formEmail"
              name="email"
              label="Email"
              placeholder="Enter Email Address"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formPhone"
              name="phone"
              label="Phone Number"
              placeholder="Enter Phone Number"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
        </Row>
        <Row>
          <label>
            <b>
              <u>Address :</u>
            </b>
          </label>
        </Row>
        <Row>
          <Col xs={4}>
            <FormControl
              controlId="formDoorNo"
              label="Door #"
              name="doorNo"
              placeholder="Enter Door #"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formStreetName"
              name="streetName"
              label="Street Name"
              placeholder="Enter Street Name"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formCity"
              name="city"
              label="City"
              placeholder="Enter City"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
        </Row>
        <Row>
          <Col xs={4}>
            <FormControl
              controlId="formState"
              name="state"
              label="State"
              placeholder="Enter State"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formCountry"
              name="country"
              label="Country"
              placeholder="Enter Country"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
          <Col xs={4}>
            <FormControl
              controlId="formZip"
              name="pincode"
              label="Zip Code"
              placeholder="Enter Zip"
              onChange = {this.onChange}
              ></FormControl>
          </Col>
        </Row>
        <Row>
          <Col className="col-md-4">
            <Button
              as="a"
              variant="success"
              style={{ display: "block" }}
              onClick={this.handleSubmit}
              type="submit"
            >
              Save
            </Button>
          </Col>
          <Col className="col-md-4">
            <Button
              as="a"
              variant="warning"
              style={{ display: "block" }}
              //   onClick={this.handleSubmit}
              type="cancel"
            >
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    );
  }
}

export default UserForm;
